<script language="javascript" type="text/javascript">
    var clickedControl = "";
    $(document).ready(function () {
        if (qsParams.q.length == 0 && qsParams.r.length == 0) { //default view. q param is not passed
            if (defaultFare.DealSets != null) {
                setDefaultView();
            } else {
                $(".dealFilterArea").hide();
                $("#dealRoot").html('<div class="dealFilterLabel"><h4>Sorry, no deals are active today. Please check this page later.<h4></div>');
                $("#dealRoot").addClass("dealFilterArea");
            }
        }
        else {
            populateCitySpecificFares();
            populateCityDropdown("selOrig", cityData.OrigCities);
            $('#selOrig').val(qsParams.q);

            populateCityDropdown("selDest", cityData.DestCities);
            $('#selDest').val(qsParams.r);
        }

        hideTerms(true);

    });



    function hideTerms(firstTimeLoad) {
        if (qsParams.terms.length == 0 || qsParams.terms != 'true') {
            $(".dealTermsConditions").hide();
        }
        else if (!firstTimeLoad) {
            $(".dealTermsConditions").hide();
        }


        $(".TermsLink").click(function () {
            $(this).next(".dealTermsConditions").slideToggle({ marginTop: '0', marginBottom: '0' }, 300);
        });

    }

    function setDefaultView() {

        populateCityDropdown("selOrig", cityData.OrigCities);
        populateCityDropdown("selDest", cityData.DestCities);
        $("#dealRoot").html("");
        populateSampleFares();
    }

    function populateSampleFares() {
        if ($.isArray(defaultFare.FareRows)) {
            for (var i = 0; i < defaultFare.FareRows.length; i++)
                populateDealRow(defaultFare, defaultFare.FareRows[i]);
        }
        else
            populateDealRow(defaultFare, defaultFare.FareRows);
    } 

    function populateDealRow(source, fareRow) {

         
        var dealSet = getDealSetByCode(source, fareRow.UniqueDealCode);
        var dealUrl = getFareCalendarUrl(fareRow.OrigCode, fareRow.DestCode, dealSet.TravelStartDate, dealSet.TravelEndDate, dealSet.DealCode, 'FlightDeals', '');

        var dealPrice = fareRow.Fare;
        var row = $("#templateDeal").clone();

        var startDateFormatted = getFormattedDate(dealSet.TravelStartDate);
        var endDateFormatted = getFormattedDate(dealSet.TravelEndDate);
        $(".brown", row).html("<a href='" + dealUrl + "'>" + fareRow.OrigName + " to " + fareRow.DestName + "</a>");
        $(".dealOfferImage", row).html("<a href='" + dealUrl + "'><img src='/~/media/images/photos-infographics/destinations/deals/deal-" + fareRow.DestCode + ".ashx' alt= '" + fareRow.DestName + "' title= '" + fareRow.DestName + "' id='dealOfferImage' class='squareShadow'/></a>");
        $(".dealFlightCopy", row).html("<span class='dealFlightDate'>" + startDateFormatted + " &ndash; " + endDateFormatted + "</span><br/>" + dealSet.Description);
        $(".dealTermsLink", row).html("<div class='TermsLink'><a title='Deal terms' href='javascript:void(0);' class='smallLink'>*Additional restrictions apply</a></div><div class='dealTermsConditions'>" + dealSet.Terms + "</div>");
        $(".dealFlightDollar", row).html("<span class='dealFlightDollarSign'>$</span>" + dealPrice + "<span class='dealFlightDollarSign'>*</span>");
                

        $(".dealLearnMore", row).html("<a href='" + dealUrl + "' class='buttonPrimarySmall'>SHOP NOW</a>");

        row.show();
        $("#dealRoot").append(row);
    }

 

    function getDealSetByCode(source, uniqueDealCode) {
        if ($.isArray(source.DealSets)) {
            for (var i = 0; i < source.DealSets.length; i++) {
                if (source.DealSets[i].UniqueDealCode == uniqueDealCode) {
                    return source.DealSets[i];
                }
            }
        }
        else
            return source.DealSets;
    }

    function showSelectedCityFares(source) { 
        var orig = $("#selOrig").val();
        var dest = $("#selDest").val();

        var path = $("#maincontent_0_dealcontent_0__contentPath").html();

        if (orig == 'default' && dest == 'default') {
            setDefaultView();
            hideTerms(false);
            return;
        }

        populateFlightDeals(orig, dest, source[0].id, path);
    }

    function populateFlightDeals(originCode, destinationCode, source, path) {
        clickedControl = source; //set source control name to global var
        $.ajax({
            url: '/content/handlers/flight-deals.ashx',
            type: 'POST',
            data: { source: 'FlightDealsSublayout', origCity: originCode, destCity: destinationCode, contentPath: path },
            contextType: 'application/json; charset=utf-8',
            dataType: 'json',
            success: flightDealsSucceeded,
            error: flightDealsFailed
        });
    }

    function flightDealsSucceeded(result) {

        $("#dealRoot").html("");
        $.each(result.FareRows, function (key, value) {
            populateDealRow(result, value);
        });

        if (clickedControl == "selOrig")
            populateCityDropdown("selDest", result.DestinationCities);

        if (clickedControl == "selDest")
            populateCityDropdown("selOrig", result.OriginCities);

        hideTerms(false);
        
    }

    function flightDealsFailed(xml, textStatus, errorThrown) {
        $("#dealRoot").html("<h2>" + xml.status + "||" + errorThrown + "</h2>");
    }

    function populateCitySpecificFares() {
        if (cityFare.FareRows != null) {
            $.each(cityFare.FareRows, function (key, value) {
                populateDealRow(cityFare, value);
            });
        }

        populateCityDropdown("selDest", cityFare.DestinationCities);
    }

    function showDealTerms(src, id) {
        $('#DealTerms' + id).showLightBox({ src: src, width: 510 });
    }
</script>
